import React, { useEffect, useState } from 'react';

import Modal from '../modal/Modal';
import DismissModal from '../modal/DismissModal';

import useApi from '../../shared/api';
import { useModalContext } from '../../context/ModalContext';
import { useUserContext } from '../../context/UserContext';
import { useLoadingContext } from '../../context/LoadingContext';
import Container from '../container/Container';
import './PaymentPending.scss';

const defaultState = { reference: '', status: '', url: '', display_text: '', text: '' };

// handles redirections to bank url
const PaymentPending = () => {
  const { API } = useApi();
  const { loading } = useLoadingContext();
  const { payment, setPayment, setCard } = useUserContext();
  const [inlineLoading, setInlineLoading] = useState(false);
  const [state, setState] = useState({ reference: '', status: '', url: '', display_text: '', text: '' });
  const { paymentPendingPopup, setPaymentPendingPopup, paymentGatewayPopup, paymentSecurityPopup, setPaymentSecurityPopup, setPaymentGatewayPopup, setModal } = useModalContext();

  const requestOtherVerification = (type) => {
    if (type !== '' && type !== 'url' && type !== 'pending' && type !== 'failed' && !paymentSecurityPopup) {
      setPaymentSecurityPopup(true);
      setPaymentPendingPopup(false);
    } else if (type === 'url' && !paymentGatewayPopup) {
      setPaymentPendingPopup(false);
      setPaymentGatewayPopup(true);
    }
  };

  const handleClick = (incl) => {
    if (incl) setPayment(null);
    setModal(false);
    setPaymentPendingPopup(false);
  };

  const saveCardAndClosePopup = (response) => {
    setCard(response.data.newCard);
    setPayment(null);
    loading(false);
    handleClick();
  };

  const handleSubmit = async () => {
    setInlineLoading(true);
    try {
      loading(true);
      const response = await API.card.get(state.reference);
      if (response.status === 201) return saveCardAndClosePopup(response);
      alert('double click if you really want to close');
      setInlineLoading(false);
      setPayment({ ...payment, ...response.data.data });
      loading(false);
    } catch (error) {
      setInlineLoading(false);
      loading(false);
    }
  };

  useEffect(() => {
    const text = payment ? payment.status.split('send_').join('') : '';
    requestOtherVerification(text);
    setState({ ...state, ...defaultState, ...payment, text });
    if (text === 'pending') handleSubmit();
  }, [payment]);

  return (
    <Modal show={paymentPendingPopup} bodyClassName="FullWidth" onClickOut={() => {}}>
      <div onDoubleClick={() => handleClick(true)} className="text-end">
        <DismissModal onClick={handleSubmit} />
      </div>
      <Container>
        <h5 className="text-center">Submittion Pending</h5>
        <h6 className="text-center">Please hold on while your transaction is confirmed </h6>
        {inlineLoading && <img className="Payment-Pending--loading" id="close-spinner" style={{ display: 'block', margin: '0% auto;' }} src="data:image/gif;base64," alt="loading ..." />}
      </Container>
    </Modal>
  );
};

export default PaymentPending;
